// Cargar el catálogo general o de una categoría específica
const itemsPorPagina = 12;

function cargarCatalogo(categoria, pagina = 1) {
    const contenedor = document.querySelector('.row');
    contenedor.innerHTML = ''; // Limpiar contenedor

    const items = catalogoCategorias[categoria];

    // Mezclar los elementos antes de cargarlos
    const itemsMezclados = mezclarArray([...items]); // Usar un nuevo array para no modificar el original

    // Paginación
    const inicio = (pagina - 1) * itemsPorPagina;
    const fin = inicio + itemsPorPagina;
    const itemsPaginados = itemsMezclados.slice(inicio, fin);

    itemsPaginados.forEach(item => {
        const card = `
            <div class="col">
                <div class="card border-info text-black h-100">
                    <img src="${item.img}" class="card-img-top polera-image" data-title="${item.title}" data-description="${item.description}" alt="${item.title}">
                    <div class="card-body">
                        <h5 class="card-title">${item.title}</h5>
                        <p class="card-text d-none">${item.description}</p>
                    </div>
                </div>
            </div>
        `;
        contenedor.innerHTML += card;
    });

    // Generar paginación
    generarPaginacion(itemsMezclados, itemsPorPagina, pagina, categoria);
}

function generarPaginacion(itemsMezclados, itemsPorPagina, pagina, categoria) {
    const numPaginas = Math.ceil(itemsMezclados.length / itemsPorPagina);
    const paginacion = document.querySelector('.pagination');
    paginacion.innerHTML = '';

    const maxVisiblePages = 5; // Número máximo de páginas visibles a la vez
    let startPage = Math.max(1, pagina - Math.floor(maxVisiblePages / 2));
    let endPage = Math.min(numPaginas, startPage + maxVisiblePages - 1);

    if (endPage - startPage < maxVisiblePages - 1) {
        startPage = Math.max(1, endPage - maxVisiblePages + 1);
    }

    // Botón "Anterior"
    if (pagina > 1) {
        const prevItem = `
            <li class="page-item">
                <a class="page-link" href="?category=${categoria}&page=${pagina - 1}">Anterior</a>
            </li>`;
        paginacion.innerHTML += prevItem;
    }

    // Números de página
    for (let i = startPage; i <= endPage; i++) {
        const paginaItem = `
            <li class="page-item ${i === pagina ? 'active' : ''}">
                <a class="page-link" href="?category=${categoria}&page=${i}">${i}</a>
            </li>`;
        paginacion.innerHTML += paginaItem;
    }

    // Botón "Siguiente"
    if (pagina < numPaginas) {
        const nextItem = `
            <li class="page-item">
                <a class="page-link" href="?category=${categoria}&page=${pagina + 1}">Siguiente</a>
            </li>`;
        paginacion.innerHTML += nextItem;
    }

    // Ajustar la posición de la paginación
    paginacion.style.marginTop = '20px';
}

document.addEventListener('DOMContentLoaded', () => {
    const urlParams = new URLSearchParams(window.location.search);
    const categoria = urlParams.get('category') || 'todo';
    const pagina = parseInt(urlParams.get('page')) || 1;
    cargarCatalogo(categoria, pagina);
});

document.addEventListener('click', (e) => {
    if (e.target.classList.contains('polera-image')) {
        const imgSrc = e.target.src;
        const title = e.target.dataset.title;
        const description = e.target.dataset.description;

        // Configurar el contenido del modal
        document.getElementById('modalImage').src = imgSrc;
        document.getElementById('modalTitle').textContent = title;
        document.getElementById('modalDescription').textContent = description;

        // Mostrar el modal
        const modal = new bootstrap.Modal(document.getElementById('poleraModal'));
        modal.show();
    }
});

document.getElementById('viewDescription').addEventListener('click', () => {
    const fullDescriptionModal = new bootstrap.Modal(document.getElementById('fullDescriptionModal'));
    document.getElementById('fullDescriptionModalTitle').textContent = document.getElementById('modalTitle').textContent;
    document.getElementById('fullDescriptionModalBody').textContent = document.getElementById('modalDescription').textContent;
    fullDescriptionModal.show();
});
